$(function(){if(!window.location.hash){$("#navbarid").load("content/navbar.html");$("#contentid").load("content/content_home.html");$("#wellid").load("content/well_home.html");$("#bottomid").load("content/sitemap.html");$(".carousel").carousel({interval:8E3})}});$(".nav-link").click(function(e){e.preventDefault();var link=$(this);var href=link.attr("href");$("html,body").animate({scrollTop:$(href).offset().top-80},500);link.closest(".navbar").find(".navbar-toggle:not(.collapsed)").click()});
function content_preload(){$("#carouselid").load("content/empty.html");$("head").append('<link rel="stylesheet" href="css/content.css" type="text/css" />');$("#navbarid").load("content/navbar.html");$("#wellid").load("content/empty.html");$("#bottomid").load("content/sitemap.html")}function loadContent(page){content_preload();$("#contentid").load("content/"+page+".html")}
$(function(){if(window.location.hash){var hash=window.location.hash.substring(1);$("#navbarid").load("content/navbar.html");$("head").append('<link rel="stylesheet" href="css/content.css" type="text/css" />');$("#carouselid").load("content/empty.html");$("#contentid").load("content/"+hash+".html");$("#wellid").load("content/empty.html");$("#bottomid").load("content/sitemap.html");$(".nav-link").hide()}});
$(window).bind("hashchange",function(){if(window.location.hash){$("#navbarid").load("content/navbar.html");var hash=window.location.hash.substring(1);$("head").append('<link rel="stylesheet" href="css/content.css" type="text/css" />');$("#carouselid").load("content/empty.html");$("#contentid").load("content/"+hash+".html");$("#wellid").load("content/empty.html");$("#bottomid").load("content/sitemap.html");$(".nav-link").hide();$("html, body").animate({scrollTop:0},0)}else location.reload()});
